import React, { useState } from 'react';
import { useCloud } from '../../context/CloudContext';
import './FileList.css';

const FileList = ({ files, view }) => {
  const { downloadFile, deleteFile, renameFile } = useCloud();
  const [selectedFile, setSelectedFile] = useState(null);
  const [showRenameModal, setShowRenameModal] = useState(false);
  const [newFileName, setNewFileName] = useState('');

  const formatBytes = (bytes) => {
    if (!bytes) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString();
  };

  const getFileIcon = (mimeType) => {
    if (!mimeType) return 'üìÑ';
    if (mimeType.includes('image')) return 'üñºÔ∏è';
    if (mimeType.includes('video')) return 'üé•';
    if (mimeType.includes('audio')) return 'üéµ';
    if (mimeType.includes('pdf')) return 'üìï';
    if (mimeType.includes('folder')) return 'üìÅ';
    if (mimeType.includes('sheet') || mimeType.includes('excel')) return 'üìä';
    if (mimeType.includes('document') || mimeType.includes('word')) return 'üìù';
    return 'üìÑ';
  };

  const handleDownload = async (file) => {
    try {
      await downloadFile(file.id, file.fileName);
    } catch (error) {
      alert('Failed to download file: ' + error.message);
    }
  };

  const handleDelete = async (file) => {
    if (window.confirm(`Are you sure you want to delete "${file.fileName}"?`)) {
      try {
        await deleteFile(file.id);
      } catch (error) {
        alert('Failed to delete file: ' + error.message);
      }
    }
  };

  const handleRenameClick = (file) => {
    setSelectedFile(file);
    setNewFileName(file.fileName);
    setShowRenameModal(true);
  };

  const handleRenameSubmit = async () => {
    if (!newFileName.trim()) {
      alert('File name cannot be empty');
      return;
    }

    try {
      await renameFile(selectedFile.id, newFileName);
      setShowRenameModal(false);
      setSelectedFile(null);
      setNewFileName('');
    } catch (error) {
      alert('Failed to rename file: ' + error.message);
    }
  };

  if (!files || files.length === 0) {
    return (
      <div className="empty-files">
        <p>No files found. Upload your first file!</p>
      </div>
    );
  }

  if (view === 'grid') {
    return (
      <>
        <div className="file-grid">
          {files.map((file) => (
            <div key={file.id} className="file-card">
              <div className="file-icon">{getFileIcon(file.mimeType)}</div>
              <div className="file-info">
                <p className="file-name" title={file.fileName}>
                  {file.fileName}
                </p>
                <p className="file-size">{formatBytes(file.fileSize)}</p>
              </div>

              <div className="file-actions">
                <button
                  className="action-btn download"
                  onClick={() => handleDownload(file)}
                  title="Download"
                >
                  ‚¨áÔ∏è
                </button>
                <button
                  className="action-btn rename"
                  onClick={() => handleRenameClick(file)}
                  title="Rename"
                >
                  ‚úèÔ∏è
                </button>
                <button
                  className="action-btn delete"
                  onClick={() => handleDelete(file)}
                  title="Delete"
                >
                  üóëÔ∏è
                </button>
              </div>
            </div>
          ))}
        </div>

        {showRenameModal && (
          <div className="modal-overlay" onClick={() => setShowRenameModal(false)}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <h3>Rename File</h3>
              <input
                type="text"
                value={newFileName}
                onChange={(e) => setNewFileName(e.target.value)}
                placeholder="Enter new file name"
                autoFocus
              />
              <div className="modal-actions">
                <button onClick={handleRenameSubmit} className="btn-primary">
                  Rename
                </button>
                <button
                  onClick={() => setShowRenameModal(false)}
                  className="btn-secondary"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}
      </>
    );
  }

  return (
    <>
      <div className="file-list-table">
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Size</th>
              <th>Modified</th>
              <th>Type</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {files.map((file) => (
              <tr key={file.id}>
                <td>
                  <span className="file-icon-small">{getFileIcon(file.mimeType)}</span>
                  {file.fileName}
                </td>
                <td>{formatBytes(file.fileSize)}</td>
                <td>{formatDate(file.modifiedAt)}</td>
                <td>{file.mimeType || 'Unknown'}</td>
                <td>
                  <div className="table-actions">
                    <button
                      className="action-btn-small download"
                      onClick={() => handleDownload(file)}
                      title="Download"
                    >
                      ‚¨áÔ∏è
                    </button>
                    <button
                      className="action-btn-small rename"
                      onClick={() => handleRenameClick(file)}
                      title="Rename"
                    >
                      ‚úèÔ∏è
                    </button>
                    <button
                      className="action-btn-small delete"
                      onClick={() => handleDelete(file)}
                      title="Delete"
                    >
                      üóëÔ∏è
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showRenameModal && (
        <div className="modal-overlay" onClick={() => setShowRenameModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>Rename File</h3>
            <input
              type="text"
              value={newFileName}
              onChange={(e) => setNewFileName(e.target.value)}
              placeholder="Enter new file name"
              autoFocus
            />
            <div className="modal-actions">
              <button onClick={handleRenameSubmit} className="btn-primary">
                Rename
              </button>
              <button
                onClick={() => setShowRenameModal(false)}
                className="btn-secondary"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default FileList;
